<?php/** Smarty plugin* -------------------------------------------------------------* Fichier :  block.has_permission.php* Type :     bloc* Nom :      has_permission* Rôle :     Vérifie si l'utilisateur à accès à une liste d'url* -------------------------------------------------------------*/function smarty_block_has_permission($params, $content, &$smarty, &$repeat){   // n'affiche que lors de la balise fermante	if(!$repeat){		$conf = $smarty->getTemplateVars('conf');		$urls = explode(",",$params["urls"]);				foreach($urls as $url){			if($conf->hasAccess($conf->getMapping($url), $_SESSION))				return $content;		}	}}?> 